import React from 'react'
import Home from './Home';
import { Link } from 'react-router-dom';
import ".home.css" 

const Base = () => {
    return ( <div className="base-container">
        <header class="site-header">
    <nav class="navbar navbar-expand-md navbar-dark bg-steel fixed-top">
        <div class="container">
        <a class="navbar-brand mr-4" href=" {% url 'shop-home' %} ">Shopping Page</a>
        <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarToggle" aria-controls="navbarToggle" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarToggle">
            <div class="navbar-nav mr-auto">
                <Link to="/" component={Home}></Link>
            {/* <a class="nav-item nav-link" href="{% url 'shop-home'%}">Home</a> */}
            {/* <a class="nav-item nav-link" href="{% url 'shop-about'%}">About</a> */}
            </div>
            {/* <!-- Navbar Right Side --> */}
            <div class="navbar-nav">
            {/* {% comment %}  */}
            changing nav bar according to state of user  
            {/* {% endcomment %} */}
            {/* {% if user.is_authenticated %} */}

                <a class="nav-item nav-link" href="{% url 'user-profile' %}">Profile</a>
                {/* <a class="nav-item nav-link" href="{% url 'user-logout' %}">Logout</a> */}
            
            {/* {% else %} */}
                {/* <a class="nav-item nav-link" href="{% url 'user-login' %}">Login</a> */}
                {/* <a class="nav-item nav-link" href="{% url 'user-register' %}">Register</a> */}
            {/* {% endif %} */}
            
            </div>
        </div>
        </div>
    </nav>
    </header>
    <main role="main" class="container">
    <div class="row">
        <div class="col-md-8">
        
        {/* {% if messages %} */}
            {/* {%for message in messages%} */}
                <div class="alert alert-{{message.tags}}">
                    {/* {{message}} */}
                </div>
            {/* {%endfor%} */}
        {/* {%endif%} */}

        {/* {% block content %} */}
        {/* {% endblock %} */}

        </div>
        <div class="col-md-4">
        <div class="content-section">
            <h3>Our Sidebar</h3>
            <p class='text-muted'>You can put any information here you'd like.
            <ul class="list-group">
                <li class="list-group-item list-group-item-light">Latest Posts</li>
                <li class="list-group-item list-group-item-light">Announcements</li>
                <li class="list-group-item list-group-item-light">Calendars</li>
                <li class="list-group-item list-group-item-light">etc</li>
            </ul>
            </p>
        </div>
        </div>
    </div>
    </main>
    </div> );
}
 
export default Base;
