import React, {useState, useContext} from 'react';
import { AuthContext } from '../Context/AuthProvider';
import axios from 'axios';
import * as p from './CSS/addProduct.module.css';

const AddProduct = () => {
    const [pname, setPName] = useState("");
    const [tags, setTags] = useState("");
    const [description, setDescription] = useState("");
    const [sellingPrice, setSPrice] = useState("");
    const [discount, setD] = useState("");
    const [file1, setFile1] = useState({});
    const [file2, setFile2] = useState({});
    const [file3, setFile3] = useState({});
    const [file4, setFile4] = useState({});
    const {csrftoken} = useContext(AuthContext);
    function onsubmit(e){
        let dis = sellingPrice - discount*sellingPrice/100;
        var data = new FormData();
        data.append('tags', tags);
        data.append('productName', pname);
        data.append('description', description);
        data.append('sellingPrice', sellingPrice);
        data.append('discountPrice', dis);
        data.append('image1', file1);
        data.append('image2', file2);
        data.append('image3', file3);
        data.append('image4', file4);
        console.log(file4);
        // console.log(dat);
        axios.post('http://127.0.0.1:8000/registerProduct', data, {
            headers:{
                "content-type":'multipart/form-data',
                'X-Requested-With': 'XMLHttpRequest',
                'X-CSRFToken': csrftoken,
            }
        })
        .then(res => {console.log(res)})
        .catch(err => console.log(err));
    }
    return ( <div className={p['main-product-container']}>
        <div className={p["product-container"]}>
            <div className={`${p['product-name']} ${p.same}`}>
                <legend>Product Name</legend>
                <input type="text" placeholder="product name" value={pname} onChange={(e)=>{setPName(e.target.value)}}/>
            </div>
            <div className={`tags ${p.same}`}>
                <legend>Tags</legend>
                <input type="text" value={tags} onChange={(e)=>{setTags(e.target.value)}}/>
            </div>
            <div className={`description ${p.same}`}>
                <legend>Description</legend>
                <input type="text" placeholder="Description" value={description} onChange={(e)=>{setDescription(e.target.value)}}/>
            </div>
            <div className={`sellingPrice ${p.same}`}>
                <legend>Selling Price</legend>
                <input type="text" placeholder="Selling Price" value={sellingPrice} onChange={(e)=>{setSPrice(e.target.value)}}/>
            </div>
            <div className={`discount-percent ${p.same}`}>
                <span>Discount</span>
                <input type="number" min="0" max="50" name="" id="" onChange={(e)=>{setD(e.target.value)}}/>
            </div>
            <div className={`images ${p.same}`}>
                <input type="file" name="" id="" accept="image/png, image/jpeg, image/jpg" onChange={(e)=>{setFile1(e.target.files[0])}}/>
                <br />
                <input type="file" name="" id="" accept="image/png, image/jpeg, image/jpg" onChange={(e)=>{setFile2(e.target.files[0])}}/>
                <br />
                <input type="file" name="" id="" accept="image/png, image/jpeg, image/jpg" onChange={(e)=>{setFile3(e.target.files[0])}}/>
                <br />
                <input type="file" name="" id="" accept="image/png, image/jpeg, image/jpg" onChange={(e)=>{setFile4(e.target.files[0])}}/>
            </div>
            <button onClick={onsubmit}>Submit</button>
        </div>
    </div> );
}
 
export default AddProduct;
