import React from 'react'
import Home from './Home';
import { Link } from 'react-router-dom';
import "./home.css" 

const Base = () => {
    return ( <div className="base-container">
        <header className="site-header">
    <nav className="navbar navbar-expand-md navbar-dark bg-steel fixed-top">
        <div className="container">
        {/* <a className="navbar-brand mr-4" href=" {% url 'shop-home' %} ">Shopping Page</a> */}
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarToggle" aria-controls="navbarToggle" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarToggle">
            <div className="navbar-nav mr-auto">
                <Link to="/" component={Home}></Link>
            {/* <a className="nav-item nav-link" href="{% url 'shop-home'%}">Home</a> */}
            {/* <a className="nav-item nav-link" href="{% url 'shop-about'%}">About</a> */}
            </div>
            {/* <!-- Navbar Right Side --> */}
            <div className="navbar-nav">
            {/* {% comment %}  */}
            changing nav bar according to state of user  
            {/* {% endcomment %} */}
            {/* {% if user.is_authenticated %} */}

                <a className="nav-item nav-link" href="{% url 'user-profile' %}">Profile</a>
                <a className="nav-item nav-link" href="{% url 'user-logout' %}">Logout</a>
            
            {/* {% else %} */}
                {/* <a className="nav-item nav-link" href="{% url 'user-login' %}">Login</a> */}
                {/* <a className="nav-item nav-link" href="{% url 'user-register' %}">Register</a> */}
            {/* {% endif %} */}
            
            </div>
        </div>
        </div>
    </nav>
    </header>
    <main role="main" className="container">
    <div className="row">
        <div className="col-md-8">
        
        {/* {% if messages %} */}
            {/* {%for message in messages%} */}
                <div className="alert alert-{{message.tags}}">
                    {/* {{message}} */}
                </div>
            {/* {%endfor%} */}
        {/* {%endif%} */}

        {/* {% block content %} */}
        {/* {% endblock %} */}

        </div>
        <div className="col-md-4">
        <div className="content-section">
            <h3>Our Sidebar</h3>
            <p className='text-muted'>You can put any information here you'd like.</p>
            <ul className="list-group">
                <li className="list-group-item list-group-item-light">Latest Posts</li>
                <li className="list-group-item list-group-item-light">Announcements</li>
                <li className="list-group-item list-group-item-light">Calendars</li>
                <li className="list-group-item list-group-item-light">etc</li>
            </ul>
            
        </div>
        </div>
    </div>
    </main>
    </div> );
}
 
export default Base;
