import React, {useContext, useState} from 'react'
import { useHistory } from 'react-router';
import { AuthContext } from '../Context/AuthProvider';

const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const {setUser, csrftoken} = useContext(AuthContext);
    const history = useHistory();

    function onsubmit(){
        const attribute = {
            method : "GET",
            credentials :"same-origin",
            headers:{
                'Accept': 'application/json',
                'X-Requested-With': 'XMLHttpRequest',
            },
        }
        fetch("http://127.0.0.1:8000/shop",attribute)
            .then((response)=> response.json())
            .then((data)=>{
                console.log(data);
                if(data['isValid']){
                    setUser(data['username']);
                    history.push("/");
                }
            })
            .catch((err)=>console.log(err));
    }
    return (
        <div class= "content-section">
            <fieldset class="form-group">
                <legend class="border-bottom mb-4">Login</legend>
                <div className="mb-3">
                    <label className="form-label">Username*</label>
                    <input className="form-control" type="text" value={username} onChange={(e)=>setUsername(e.target.value)}/>
                    <small>Required 150 characters or fewer. Letters, digits and @/./+/-/_ only.</small>
                </div>
                <div className="mb-3">
                    <label className="form-label">Password*</label>
                    <input type="password" className="form-control" value={password} onChange={(e)=>setPassword(e.target.value)}/>
                </div>
            </fieldset>
            <div class="form-group">
                <button class="btn btn-outline-info" onClick={onsubmit} type="submit">Login</button>
            </div>
        <div class="border-top pt-3">
            <small class="text-muted">
                Need an account ? <a class="ml-2" href="{%url 'user-register'%}">Sign up Now</a>
            </small>
        </div>
    </div>
     );
}
 
export default Login;
