import React, {useState} from 'react';
import { AuthContext } from '../Context/AuthProvider';

const AddProduct = () => {
    const [pname, setPName] = useState("");
    const [tags, setTags] = useState("");
    const [description, setDescription] = useState("");
    const [sellingPrice, setSPrice] = useState("");
    const [discount, setD] = useState("");
    const [file1, setFile1] = useState(null);
    const [file2, setFile2] = useState(null);
    const [file3, setFile3] = useState(null);
    const [file4, setFile4] = useState(null);
    const {csrftoken} = useContext(AuthContext);
    function onsubmit(e){
        let dis = sellingPrice - discount*sellingPrice/100;
        const attribute = {
            method: "POST",
            credentials :"same-origin",
            headers:{
                'Accept': 'application/json',
                'X-Requested-With': 'XMLHttpRequest',
                'X-CSRFToken': csrftoken,
            },
            body: JSON.stringify({
                tags:tags,
                productName:pname,
                description:description,
                sellingPrice:sellingPrice,
                discountPrice:dis,
                image1:file1,
                image2:file2,
                image3:file3,
                image4:file4
            }),
        }
        fetch('http://127.0.0.1:8000/registerProduct', attribute)
            .then((response)=>response.json())
            .then((data)=>{
                console.log(data);
                if(data === "Product Saved"){

                }
            })
            .catch((err)=>console.log(err))
    }
    return ( <div>
        <div className="addproduct-container">
            <div className="product-name">
                <legend>Product Name</legend>
                <input type="text" placeholder="product name" value={pname} onChange={(e)=>{setPName(e.target.value)}}/>
            </div>
            <div className="tags">
                <legend>Tags</legend>
                <input type="text" value={tags} onChange={(e)=>{setTags(e.target.value)}}/>
            </div>
            <div className="description">
                <legend>Description</legend>
                <input type="text" placeholder="Description" value={description} onChange={(e)=>{setDescription(e.target.value)}}/>
            </div>
            <div className="sellingPrice">
                <legend>Selling Price</legend>
                <input type="text" placeholder="Selling Price" value={sellingPrice} onChange={(e)=>{setSPrice(e.target.value)}}/>
            </div>
            <div className="discount-percent">
                <span>Discount</span>
                <input type="number" min="0" max="50" name="" id="" onChange={(e)=>{setD(e.target.value)}}/>
            </div>
            <div className="images">
                <input type="file" name="" id="" onChange={(e)=>{setFile1(e.target.files[0])}}/>
                <br />
                <input type="file" name="" id="" onChange={(e)=>{setFile2(e.target.files[0])}}/>
                <br />
                <input type="file" name="" id="" onChange={(e)=>{setFile3(e.target.files[0])}}/>
                <br />
                <input type="file" name="" id="" onChange={(e)=>{setFile4(e.target.files[0])}}/>
            </div>
            <button onClick={onsubmit}>Submit</button>
        </div>
    </div> );
}
 
export default AddProduct;
