import React, {useContext} from 'react';
import {Navbar, Container, Nav, Form, FormControl, NavDropdown, Button} from 'react-bootstrap';
import './product.css';
import img1 from './img1.jpg';
import img2 from './img2.jpg';
import img3 from './img3.jpg';
import img4 from './img4.jpg';
import boat1 from './boat1.jpeg';
import noise1 from './noise1.jpeg';
import realme1 from './realme1.jpeg';
import timewear1 from './timewear1.jpeg';
// import img4 from './img4.jpg';
import noise2 from './wrb-sw-colorfitpro3-std-ros-pnk-android-ios-noise-original-imag2drhshyutzm8.jpeg';
import {FaRupeeSign} from 'react-icons/fa';
import { AuthContext } from '../Context/AuthProvider';

const Product = () => {
    const {user} = useContext(AuthContext);

    function clickCart(e){
      if(user===""){
        
      }
    }
    function onClickPhoto(e){
      let mphoto = document.querySelector('.enlarged-photo>img');
      let ophoto = document.querySelector('.p-visible');
      ophoto.classList.remove("p-visible");
      e.target.classList.add("p-visible");
      let allPhotosImg = document.querySelectorAll(".photo>img");
      if(e.target === allPhotosImg[0]){
        mphoto.src=img1;
      }
      else if(e.target === allPhotosImg[1]){
        mphoto.src=img2;
      }
      else if(e.target === allPhotosImg[2]){
        mphoto.src=img3;
      }
      else if(e.target === allPhotosImg[3]){
        mphoto.src=img4;
      }
    }
    return ( <div>
    <Navbar bg="light" variant="light">
    <Container>
    <Navbar.Brand href="#home">Navbar</Navbar.Brand>
    <Form className="d-flex">
        <FormControl
          type="search"
          placeholder="Search"
          className="me-2"
          aria-label="Search"
          style={{width:'35rem'}}
        />
        <Button variant="outline-success"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-search" viewBox="0 0 16 16">
        <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z"/>
        </svg></Button>
      </Form>
    <Nav className="me-auto" >
      <Nav.Link href="#home">Home</Nav.Link>
      
      {(user === "")?(<div style={{display:"flex"}}><NavDropdown style={{cursor:"pointer"}} title="SignUp" id="navbarScrollingDropdown">
          <NavDropdown.Item href="#action3">As a Customer</NavDropdown.Item>
          <NavDropdown.Divider />
          <NavDropdown.Item href="#action4">As a Seller</NavDropdown.Item>
        </NavDropdown>
        <NavDropdown title="Login" style={{cursor:"pointer"}} id="navbarScrollingDropdown">
          <NavDropdown.Item href="#action3">As a Customer</NavDropdown.Item>
          <NavDropdown.Divider />
          <NavDropdown.Item href="#action4">As a Seller</NavDropdown.Item>
        </NavDropdown></div>):(
        <div style={{display:"flex"}}><Nav.Link href="#features">Features</Nav.Link>
        <Nav.Link href="#pricing">Pricing</Nav.Link></div>)
      }
      
      {/* <NavDropdown title="Link" id="navbarScrollingDropdown">
          <NavDropdown.Item href="#action3">As a Customer</NavDropdown.Item>
          <NavDropdown.Divider />
          <NavDropdown.Item href="#action4">As a Seller</NavDropdown.Item>
        </NavDropdown>
        <NavDropdown title="Link" id="navbarScrollingDropdown">
          <NavDropdown.Item href="#action3">As a Customer</NavDropdown.Item>
          <NavDropdown.Divider />
          <NavDropdown.Item href="#action4">As a Seller</NavDropdown.Item>
        </NavDropdown> */}
    </Nav>
    </Container>
  </Navbar>
  <div className="product-container">
    <div className="product-photos">
      <div className="enlarged-photo">
        <img src={img1} alt="" />
