  const {isSeller, setUser, setIsSeller, setList, setSearchValue} = useContext(AuthContext);
  const history = useHistory();
    function login(e){
      if(e.target.id==='custSign'){
        history.push("/shop/customerLogin");
      }else{
        history.push("/shop/login");
      }
    }

    function signup(e){
      if(e.target.id==='custSignup'){
        history.push("/shop/customerSignup");
      }else{
        history.push("/shop/register");
      }
    }

